function calculateDelay(message) {

    if (message.text) {
      var words = message.text.split(/\s+/);
      var wpm = 180;
      var wps = (wpm / 60);
      var delay =  (words.length / wps)*1000;
      if (delay &gt; 4000) {
        delay = 4000;
      }

      return delay;

    } else {
      return 1500;
    }

  }

